content:
/*----------------------------------
LP body（本体）
\----------------------------------*/
@include theme-fo-base {
//LPヘッダー
&.lp_header_sec {
font-family: $lp_font_family;
color: $lp_text_color;
line-height: $lp_text_line_height;
letter-spacing: $lp_text_letter_spacing;
padding: 0;
@include lp-heading();
@include lp-richtext();
@include lp-form();
.inner {
padding: 10px 20px;
@include breakpoint-sp {
padding: 10px;
}
.row {
&.header_row {
display: flex;
align-items: center;
@include breakpoint-pc {
min-height: 60px;
}
.col {
padding: 0;
&.logo_col {
@include breakpoint-pc {
max-width: 300px;
min-width: 150px;
width: auto !important;
}
.component {
&.image {
margin: 0;
&.logo_img {
img {
@include breakpoint-pc {
max-width: $lp_header_logo !important;
width: 100% !important;
}
@include breakpoint-sp {
max-height: $lp_header_sp_logo !important;
}
}
}
}
}
}
&.btn_col {
display: flex !important;
justify-content: flex-end;
align-items: center;
flex: 1;
@include breakpoint-pc {
width: auto !important;
}
.component {
margin: 0;
&:not(:first-child) {
@include breakpoint-pc {
margin-left: 15px;
}
}
&.button {
margin-top: 0;
.button-cont {
a {
@include breakpoint-sp {
background: none;
width: auto;
min-width: auto;
font-size: 0 !important;
letter-spacing: 0;
border-radius: 0;
border: 0;
margin: 0;
box-shadow: none;
padding: 5px 10px 5px 5px;
}
&::before {
@include breakpoint-sp {
font-size: 22px;
line-height: 1;
color: $lp_main_color;
margin: 0;
}
}
}
}
}
}
}
}
}
&.nav_row {
@include breakpoint-pc {
margin: 20px 0 -10px;
}
.col {
padding: 0;
.component {
&.list {
margin: 0;
ul {
@include breakpoint-pc {
display: table;
width: 100%;
table-layout: fixed;
}
li {
margin: 0;
padding: 0;
font-size: 14px;
@include breakpoint-pc {
display: table-cell;
}
@include breakpoint-sp {
width: 50%;
}
a {
display: block;
font-size: inherit;
color: inherit;
text-align: center;
padding: 20px 10px;
@include breakpoint-sp {
padding: 10px 5px;
}
&:hover {
background-color: $lp_main_color;
color: #fff;
}
}
}
}
}
}
}
}
}
}
//LPコンテンツ記述
& ~ section {
@include lp-heading();
@include lp-richtext();
@include lp-form();
@include lp-mv();
@include lp-contents();
//フッター
&.lp_footer_sec {
padding: 0;
.inner {
max-width: 100% !important;
padding: 0;
.row {
padding: 0;
&:not(.copyright_row) {
max-width: $lp_max_width;
margin: 0 auto;
padding: 0 20px;
}
&.site_map_row {
padding: 40px 20px;
.col {
vertical-align: middle;
}
}
.col {
padding: 0;
.component {
&.image {
&.footLogo_img {
img {
max-width: 250px !important;
width: 100% !important;
}
}
}
&.list {
&.site_map_list {
ul {
display: flex;
gap: 10px 20px;
@include breakpoint-sp {
flex-direction: column;
}
li {
margin: 0;
padding: 0;
a {
color: inherit;
&:hover {
opacity: $lp_hover_opacity;
}
}
}
}
&.vertical {
ul {
flex-direction: column;
li {
a {
padding: 0;
}
}
}
}
}
}
&.cta {
margin: 0;
.cta-row {
padding-top: 0 !important;
&.page_top_row {
@include page_top;
& \+ .cta-row {
margin-top: -40px;
}
}
.cta-column {
padding: 0;
.component {
&.richtext {
&.foot-copyright {
@include copyright_custom;
}
}
}
}
}
}
}
}
}
}
}
}
}
//LPセクション固定
&.lp_sticky_sec {
position: sticky;
top: 0;
z-index: 1000;
box-shadow: $lp_box_shadow_nav_common;
& ~ section {
.inner {
.row {
.col {
[id] {
//ヘッダーの高さに合わせてください(ヘッダーの高さ＋componentのmargin10px）
@include breakpoint-pc {
scroll-margin-top: 80px;
}
@include breakpoint-sp {
scroll-margin-top: 55px;
}
}
}
}
}
}
}
}
/*------------- カスタム用 LP END-------------*/

//この書き方は強すぎてしまうのでNG
.row {
.example_row {
display: block !important;
}
}
//セレクタの優先度を上げて効かせる方法
.row {
.example_row:not(:root) {
display: block;
}
}
//セレクタの優先度がまだ低い場合
.row {
.example_row:not(:root):not(:root) {
display: block;
}
}

