/*--- リッチテキストパーツ ---*/
.component {
&.richtext {
:link {
text-decoration: none;
}
:visited {
text-decoration: none;
}
//リスト
li {
font-size: inherit;
a {
font-size: inherit;
}
}
//表
table {
border: none;
&[align="center"] {
margin: auto;
}
:is(thead,tbody,tr,th,td) {
border-color: $border_color;
tr {
:is(th,td) {
background-clip: padding-box; //FF,edgeでボーダー消える現象回避;
position: static; //FF,edgeでボーダー消える現象回避;
p {
font-size: inherit;
& \+ p {
margin-top: 10px !important;
}
}
:is(ul,ol) {
margin-left: 25px;
}
}
}
}
}
.normal-table {
&::after {
content: "";
display: table;
clear: both;
}
}
.table-with-row-header {
&::after {
content: "";
display: table;
clear: both;
}
}
.table-with-two-headers {
overflow: auto;
&::after {
content: "";
display: table;
clear: both;
}
}
