カスタマイズする場合はコピーして所定の場所で使用してください。
\-----------------------------------------------------*/
@include theme-fo-base {
.inner {
.row {
.col {
.component {
&.richtext {
//テーブル(ボーダー無し）
&.custom_table {
table {
border: none;
:is(thead,tbody) {
border: 0;
tr {
border: 0;
th {
vertical-align: top;
border: 0;
}
td {
vertical-align: top;
border: 0;
}
p {
@include breakpoint-tb {}
}
}
}
}
}
//スマホ時テーブル固定スクロール
&.sp_table_scroll {
:is(
.normal-table,
.table-with-row-header,
.table-with-two-headers
) {
@include breakpoint-sp {
overflow-x: auto;
-webkit-overflow-scrolling: touch;
margin-bottom: 40px;
}
&::after {
@include breakpoint-sp {
content: "\f07e";
display: inline-block;
font-family: $font_family_awsm;
font-size: 30px;
line-height: 1;
color: var(--fo-main-color);
position: absolute;
left: 50%;
margin-left: -15px;
}
}
}
table {
@include breakpoint-tb {
min-width: 130vw;
}
@include breakpoint-ssp {
min-width: 200vw;
}
thead {
tr {
th {
@include breakpoint-sp {
white-space: initial;
}
}
}
}
tbody {
tr {
th {
@include breakpoint-sp {
display: table-cell !important;
white-space: nowrap;
}
}
td {
vertical-align: top;
@include breakpoint-sp {
display: table-cell !important;
white-space: initial;
}
p {
@include breakpoint-tb {}
}
}
}
}
}
}
//英語テキスト
&.en_text {
color: var(--fo-main-color);
table {
border: none;
:is(thead,tbody) {
border: 0;
tr {
border: 0;
th {
border: 0;
padding: 0;
}
td {
border: 0;
padding: 0;
}
p {
margin: 0 !important;
@include breakpoint-tb {}
}
}
}
}
}
//ナンバー
&.number_text {
table {
width: auto !important;
border: none;
:is(thead,tbody) {
border: 0;
tr {
border: 0;
th {
border: 0;
line-height: 1;
padding: 0;
}
td {
border: 0;
line-height: 1;
padding: 0;
}
p {
margin: 0 !important;
line-height: 1;
@include breakpoint-tb {}
}
}
}
}
}
}
}
}
}
}
}
/*------------------------------
カスタムパーツ 格納場所
基本的にパーツ単体の記述でお願いします。
パーツ名を忘れず記載してください。
\-------------------------------*/
@include theme-fo-base {
.inner {
.row {
.col {
.component {}
}
}
}
}
/*--- カスタムパーツ 格納場所 end ---*/
/*----------------------------------
ユニバーサルクラス
\----------------------------------*/
//スマホだけ表示させる
.spOnly {
@include breakpoint-pc {
display: none !important;
}
}
//PCだけ表示させる
.pcOnly {
@include breakpoint-sp {
display: none !important;
}
}
//displayNoneクラス
.displayNone {
display: none !important;
}
//ギャラリーパーツ
.poptrox-overlay {
z-index: 9999 !important;
.poptrox-popup {
.pic {
z-index: 10000;
}
}
}
//iframe
.component {
&.iframe {
display: flex;
//左寄せ
&.right {
justify-content: flex-end;
}
//中央寄せ
&.center {
justify-content: center;
}
//ワイド
&.if_16-9 {
aspect-ratio: 16/9;
}
//スタンダート
&.if_4-3 {
aspect-ratio: 4/3;
}
//スクエア
&.if_1-1 {
aspect-ratio: 1/1;
}
//縦撮り
&.if_9-16 {
aspect-ratio: 9/16;
}
//シネマスコープ
&.if_235-1 {
aspect-ratio: 2.35/1;
}
//カスタムclass
&.if_custom {
width: 100%;
height: 0;
position: relative;
$var: 300;
@while $var <= 3000 {
&.pc-h#{$var} {
@include breakpoint-pc {
height: #{$var}px;
}
}
&.sp-h#{$var} {
@include breakpoint-sp {
height: #{$var}px;
}
}
$var: $var + 10;
}
iframe {
width: 100%;
height: 100%;
position: absolute;
top: 0;
left: 0;
}
}
}
}
//スムーススクロール
html {
scroll-behavior: smooth !important;
}
//フォームパーツ
.component {
&.form {
.form-title {
color: var(--fo-text-main-color);
}
//フォームの必須
.form-label {
&.require {
&::after {
padding: 2px 7px 4px !important;
margin-left: 20px;
}
}
}
input[type=checkbox],
input[type=radio] {
min-height: auto;
}
.form-submit {
.popup-link {
font-weight: $font_weight;
}
}
&.vertical {
.form-label {
&::after {
position: relative;
top: -2px;
}
}
}
&.horizontal {
.form-label {
padding-right: 10px;
&::after {
@include breakpoint-pc {
top: 8px !important;
}
@include breakpoint-sp {
top: -2px !important;
}
}
}
}
}
}
//ズレ対策
.component {
&.form {
label.agreement {
display: flex;
align-items: center;
.form-label {
padding-top: 6px;
&::after {
top: -1.5px;
}
}
}
}
}
//2カラム時、PCとSPでカラムの位置を入れ替える
.reverse {
@include breakpoint-sp {
display: flex !important;
flex-wrap: wrap-reverse;
}
}
/*------------- main（コンテンツ） END -------------*/
/* ----------------------------------
blog(ブログテンプレート)
\----------------------------------*/
/*--- ブログページ一覧 ---*/
.component {
&.blog_articles {
hr {
border: 0;
border-bottom: 1px solid $blog_border_color;
margin-bottom: 30px;
@include breakpoint-sp {
margin-bottom: 10px;
}
}
article {
.infomation {
padding: 0 0 0 30px;
//タイトル
& > h2 {
line-height: 1.5;
padding: 0;
a {
display: inline-block;
font-family: var(--fo-heading-font-family);
font-size: $list_heading_pc_fontsize;
font-weight: bold;
letter-spacing: var(--fo-heading-letter-spacing);
line-height: var(--fo-heading-line-height);
color: var(--fo-heading-main-color);
@include breakpoint-sp {
font-size: $list_heading_sp_fontsize;
}
&:hover {
opacity: $hover_opacity;
}
}
}
p {
font-size: var(--fo-text-font-size-pc);
color: var(--fo-text-main-color);
line-height: var(--fo-text-line-height);
letter-spacing: var(--fo-text-letter-spacing);
padding: 0;
@include breakpoint-sp {
font-size: var(--fo-text-font-size-sp);
}
}
//記事を見るの位置
a {
&.view-article {
display: inline-block;
width: 100%;
font-size: var(--fo-text-font-size-pc);
color: var(--fo-text-link-color,var(--fo-text-main-color));
line-height: var(--fo-text-line-height);
letter-spacing: var(--fo-text-letter-spacing);
text-align: right;
text-decoration: underline;
@include breakpoint-sp {
font-size: var(--fo-text-font-size-sp);
}
&:hover {
opacity: $hover_opacity;
text-decoration: none;
}
}
}
//日付の背景色
span {
&.published_at {
background: none;
font-size: 14px;
color: var(--fo-text-main-color);
text-align: left;
padding: .5rem 0;
}
}
//タグスタイル
.tags {
padding-left: 0;
a {
&.tag {
@include tag_custom;
i {
margin-right: 0;
&::before {
@include tag_icon_custom;
}
}
}
}
}
}
.catch,
.infomation {
@include breakpoint-sp {
width: 100%;
float: none;
padding: 1em 0 0;
}
}
.catch {
a {
img {
border-radius: $blog_border_radius;
}
}
}
}
}
}
/*--- ブログ記事詳細 ---*/
.theme-fo-base {
.clearfix {
.contents {
&.blog-contents {
padding: 0;
section {
.inner {
@include breakpoint-sp {
padding: 0 20px 10px;
}
.row {
.col {
.component {
&.blog_article {
margin-top: 0;
// 日付・タグ
.article-meta-info {
opacity: unset;
span {
&.post-date {
time {
font-family: var(--fo-text-font-family);
}
}
&.article-category {
i {
display: none;
}
a {
color: var(--fo-main-color);
&::before {
@include category_icon_custom;
}
&:hover {
opacity: $hover_opacity;
}
}
}
&.article-tags {
i {
display: none;
}
a {
@include tag_custom;
display: inline-block;
margin-top: 10px;
&::before {
@include tag_icon_custom;
}
&::after {
content: none;
}
}
}
}
}
// アイキャッチ
.article-eyecatch {
@include breakpoint-pc {
margin-bottom: 30px;
}
@include breakpoint-sp {
margin: 30px 0;
}
}
.article-post {
color: var(--fo-text-main-color);
padding: 0;
// ブログ詳細の本文
.component {
&.richtext {
// 引用
blockquote {
font-size: inherit;
color: inherit;
padding: 40px 30px;
@include breakpoint-sp {
padding: 20px;
}
&::before {
font-size: 500%;
}
}
}
}
}
section {
//著者
&.author {
padding: 0;
border-color: $border_color;
@include breakpoint-sp {
display: flex;
padding: 15px;
}
.author-image {
margin-right: 15px;
@include breakpoint-sp {
width: 50%;
}
img {
border-radius: $blog_border_radius;
}
}
.author-info {
.author-name {
font-family: var(--fo-heading-font-family);
font-size: 18px;
color: var(--fo-heading-main-color);
line-height: var(--fo-heading-line-height);
letter-spacing: var(--fo-heading-letter-spacing);
@include breakpoint-sp {
font-size: 16px;
}
}
.author-description {
white-space: pre-wrap;
font-size: var(--fo-text-font-size-pc);
letter-spacing: var(--fo-text-letter-spacing);
line-height: var(--fo-text-line-height);
color: var(--fo-text-main-color);
@include breakpoint-sp {
font-size: var(--fo-text-font-size-sp);
}
}
}
}
}
}
}
section {
//ページネーション
&.prev-next-link {
display: flex;
gap: calc(40px * var(--fo-spacing-coef));
@include breakpoint-sp {
flex-direction: column;
gap: 0;
margin: calc((40px * var(--fo-spacing-coef)) * .75) 0 20px;
padding: 0;
}
a {
color: var(--fo-text-main-color);
border-color: $border_color;
&:hover {
background: transparent;
opacity: $hover_opacity;
}
&::before,
&::after {
color: var(--fo-main-color);
}
//前の記事 次の記事
h3 {
&.prev-next-label {
font-family: var(--fo-heading-font-family);
font-size: 18px;
color: var(--fo-heading-main-color);
line-height: var(--fo-heading-line-height);
letter-spacing: var(--fo-heading-letter-spacing);
@include breakpoint-sp {
font-size: 16px;
}
}
}
div.thumb-wrap {
display: flex;
align-items: flex-start;
img {
float: none;
margin-right: 15px;
}
p {
max-height: none;
font-size: 14px;
letter-spacing: var(--fo-text-letter-spacing);
line-height: var(--fo-text-line-height);
color: var(--fo-text-main-color);
display: -webkit-box;
-webkit-line-clamp: 2;
-webkit-box-orient: vertical;
text-overflow: ellipsis;
overflow: hidden;
}
}
}
}
}
}
}
}
}
}
}
}
}
/*------------- blog(ブログテンプレート) END -------------*/
/* ----------------------------------
カテゴリ・タグ自動生成ページ
\----------------------------------*/
.theme-fo-base {
&.clearfix {
.wrapper {
&.clearfix {
.contents {
:is(
.category-preview,
.tag-preview
) {
box-sizing: border-box;
margin: auto;
article {
display: table;
border-bottom: 1px solid $blog_border_color;
@include breakpoint-sp {
display: block;
width: 100%;
margin: 20px 0;
padding: 0;
}
.article-thumbnail {
display: table-cell;
width: 30%;
padding-bottom: 10px;
@include breakpoint-sp {
display: block;
width: 100%;
}
a {
display: block;
@include breakpoint-sp {
margin-bottom: 10px;
}
img {
width: 100%;
border-radius: 0;
}
}
}
.article-cont {
display: table-cell;
width: 70%;
height: auto;
vertical-align: top;
padding: 10px 30px;
@include breakpoint-sp {
display: block;
width: 100%;
margin-bottom: 20px;
padding: 0;
}
.article-title {
overflow: initial;
max-height: none;
a {
font-family: var(--fo-heading-font-family);
font-size: $list_heading_pc_fontsize;
font-weight: inherit;
color: var(--fo-heading-main-color);
line-height: var(--fo-heading-line-height);
letter-spacing: var(--fo-heading-letter-spacing);
@include breakpoint-sp {
font-size: $list_heading_sp_fontsize;
}
&:hover {
opacity: $hover_opacity;
}
}
}
.article-meta-info {
position: relative;
.article-category {
&::before {
content: none;
}
a {
color: var(--fo-text-link-color,var(--fo-text-main-color));
&::before {
@include category_icon_custom;
}
&:hover {
opacity: $hover_opacity;
}
}
}
//タグ自動生成のタグスタイル
.tag-lists {
@include breakpoint-sp {
display: block;
}
.article-tags {
@include tag_custom;
&::before {
@include tag_icon_custom;
}
}
}
//カレンダーアイコン削除
span {
&.post-date {
color: var(--fo-text-main-color);
&::before {
content: none;
}
time {
font-family: var(--fo-text-font-family);
}
}
&.article-author {
color: var(--fo-text-main-color);
}
}
}
}
}
}
}
}
}
}
}
:is(
.tag-preview article .article-cont .article-meta-info .tag-lists,
.category-preview article .article-cont .article-meta-info .tag-lists
) {
max-height: 100% !important;
}
