/*----------------------------------
side bar（サイドバー）
\----------------------------------*/
//834幅から１カラム設定
.theme-fo-base {
.wrapper {
// 左右中央サイドバー設定共通
&:is(.side-left,.side-both,.side-right) {
max-width: $max_width;
.contents {
background: transparent;
max-width: unset;
border-left: 0;
border-right: 0;
padding: 0;
// セクションパーツ対策
.article-post {
& > section {
&:first-child {
&[data-designpart-id] {
padding-top: 0;
}
}
}
}
section {
.inner {
padding: 20px;
}
}
}
.side {
// 左右サイドバーコンテンツ内共通
&:is(.left,.right) {
@include breakpoint-pro_s {
display: block;
width: 100%;
}
@include breakpoint-tb {
width: 100%;
margin: auto;
}
@include breakpoint-msp {
padding: 0;
}
.inner {
@include breakpoint-pro {
padding: 20px 30px;
}
@include breakpoint-sp {
padding: 20px;
}
.component:not(:root:root) {
margin: 20px 0;
&:first-child {
margin-top: 10px;
}
&.heading {
h1 {
@include breakpoint-pc {
font-size: $blog_title_h1_fontsize_pc;
}
@include breakpoint-sp {
