display: block; //崩れ対策
}
}
}
// 右サイドバー設定
&.side-left {}
// 中央サイドバーセクション
&.side-both {
@include breakpoint-pro_s {
display: flex;
flex-wrap: wrap;
}
.contents {
@include breakpoint-pc {
width: 100%;
}
@include breakpoint-pro_s {
display: block;
max-width: none;
order: 1;
padding: 10px 0;
}
@include breakpoint-sp {
padding: 10px 0;
}
section {
.inner {
.row {
.col {
.component {
&.form {
.form-submit {
.popup-link {
@include breakpoint-pro {
min-width: 100%;
}
}
}
}
}
}
}
}
}
}
.side {
