//padding 一方向
@mixin padding-property($property:top,$value:20px,$device:all) {
  @if($device == all) {
    @include breakpoint-pc {
      padding-#{$property}: calc(#{$value} * var(--fo-spacing-coef));
    }
    @include breakpoint-sp {
      padding-#{$property}: calc((#{$value} * var(--fo-spacing-coef)) * .75);
    }
  }
  @else if($device == pc) {
    @include breakpoint-pc {
      padding-#{$property}: calc(#{$value} * var(--fo-spacing-coef));
    }
  }
  @else if($device == sp) {
    @include breakpoint-sp {
      padding-#{$property}: calc(#{$value} * var(--fo-spacing-coef));
    }
  }
}
//padding上下左右
