//隣接要素のmargin
@mixin adjacentMargin($className:row,$marginTop:46px,$device:all) {
  & \+ .#{$className} {
    @if($device == all) {
      @include breakpoint-pc {
        margin-top: unquote('max(calc(#{$marginTop} * var(--fo-spacing-coef)),0px)');
