      .quote-link {
        &:hover {
          opacity: $hover_opacity;
        }
        .quote-link_entry {
          .quote-link-img {
            .quote-link-img-item {
              img {
                height: 150px;
                position: static;
                object-fit: cover;
                &:hover {
