// 検索結果画面
.theme-fo-base {
  .wrapper {
    .contents {
      & > .component {
        &.search {
          @include search_container;
          & \+ .entry-list {
            padding-bottom: 25px;
            .entry-list-unit {
              border-bottom: 1px solid $border_color;
              margin: 10px 0;
              padding: 10px 0;
              @include breakpoint-pc {
                display: flex;
              }
              &:not(:has(.img)) {
                .cont {
                  width: 100%;
                }
              }
              .img {
                @include breakpoint-pc {
                  width: 30%;
                  padding: 0 0 10px 0;
                }
                @include breakpoint-sp {
                  padding-bottom: 10px;
                }
                img {
                  border-radius: $case_border_radius;
                  &:hover {
                    opacity: $hover_opacity;
                  }
                }
              }
              .cont {
                border-left: 0;
                @include breakpoint-pc {
                  width: 70%;
                  padding: 0 10px 10px;
                }
                h3 {
                  max-height: none;
                  line-height: initial;
                  @include breakpoint-sp {
                    padding: 0;
                  }
                  a {
                    font-family: var(--fo-heading-font-family);
                    font-size: $list_heading_pc_fontsize;
                    color: var(--fo-heading-main-color);
                    line-height: var(--fo-heading-line-height);
                    letter-spacing: var(--fo-heading-letter-spacing);
                    padding: 0 10px;
                    display: -webkit-box;
                    -webkit-line-clamp: 3;
                    -webkit-box-orient: vertical;
                    text-overflow: ellipsis;
                    overflow: hidden;
                    @include breakpoint-sp {
                      font-size: $list_heading_sp_fontsize;
                      padding: 0;
                    }
                    &:hover {
                      text-decoration: none;
                      opacity: $hover_opacity;
                    }
                  }
                }
