      & > :is(.clearfix,.main-visual-content) {
        min-height: $sub_mv_height;
        .row {
          \--fo-heading-main-color: #fff;
          \--fo-text-main-color: #fff;
          \--fo-text-link-color: #fff;
          .col {
            .component {
              &.cta {
