/*---------------------------------------------------
ここのパーツは自由に使用してください。
カスタマイズする場合はコピーして所定の場所で使用してください。
\-----------------------------------------------------*/
@include theme-fo-base {
  .inner {
    .row {
      .col {
        .component {
          &.richtext {
            //テーブル(ボーダー無し）
            &.custom_table {
              table {
                border: none;
                :is(thead,tbody) {
                  border: 0;
                  tr {
                    border: 0;
                    th {
                      vertical-align: top;
                      border: 0;
                    }
                    td {
                      vertical-align: top;
                      border: 0;
                    }
                    p {
                      @include breakpoint-tb {}
                    }
                  }
                }
              }
            }
            //スマホ時テーブル固定スクロール
            &.sp_table_scroll {
              :is(
                .normal-table,
                .table-with-row-header,
                .table-with-two-headers
              ) {
                @include breakpoint-sp {
                  overflow-x: auto;
                  -webkit-overflow-scrolling: touch;
                  margin-bottom: 40px;
                }
                &::after {
                  @include breakpoint-sp {
                    content: "\f07e";
                    display: inline-block;
                    font-family: $font_family_awsm;
                    font-size: 30px;
                    line-height: 1;
                    color: var(--fo-main-color);
                    position: absolute;
                    left: 50%;
                    margin-left: -15px;
                  }
                }
              }
              table {
                @include breakpoint-tb {
                  min-width: 130vw;
                }
                @include breakpoint-ssp {
                  min-width: 200vw;
                }
                thead {
                  tr {
                    th {
                      @include breakpoint-sp {
                        white-space: initial;
                      }
                    }
                  }
                }
                tbody {
                  tr {
                    th {
                      @include breakpoint-sp {
                        display: table-cell !important;
                        white-space: nowrap;
                      }
                    }
                    td {
                      vertical-align: top;
                      @include breakpoint-sp {
                        display: table-cell !important;
                        white-space: initial;
                      }
                      p {
                        @include breakpoint-tb {}
                      }
                    }
                  }
                }
              }
            }
            //英語テキスト
            &.en_text {
              color: var(--fo-main-color);
              table {
                border: none;
                :is(thead,tbody) {
                  border: 0;
                  tr {
                    border: 0;
                    th {
                      border: 0;
                      padding: 0;
                    }
                    td {
                      border: 0;
                      padding: 0;
                    }
                    p {
                      margin: 0 !important;
                      @include breakpoint-tb {}
                    }
                  }
                }
              }
            }
            //ナンバー
            &.number_text {
              table {
                width: auto !important;
                border: none;
                :is(thead,tbody) {
                  border: 0;
                  tr {
                    border: 0;
                    th {
                      border: 0;
                      line-height: 1;
                      padding: 0;
                    }
                    td {
                      border: 0;
                      line-height: 1;
                      padding: 0;
                    }
                    p {
                      margin: 0 !important;
                      line-height: 1;
                      @include breakpoint-tb {}
                    }
                  }
                }
              }
            }
          }
        }
      }
    }
  }
}
