/*--- カスタムパーツ 格納場所 end ---*/
/*----------------------------------
    ユニバーサルクラス
\----------------------------------*/
//スマホだけ表示させる
.spOnly {
  @include breakpoint-pc {
    display: none !important;
  }
}
//PCだけ表示させる
.pcOnly {
  @include breakpoint-sp {
    display: none !important;
  }
}
//displayNoneクラス
.displayNone {
  display: none !important;
}
//ギャラリーパーツ
.poptrox-overlay {
  z-index: 9999 !important;
  .poptrox-popup {
    .pic {
      z-index: 10000;
    }
  }
}
//iframe
.component {
  &.iframe {
    display: flex;
    //左寄せ
    &.right {
      justify-content: flex-end;
    }
    //中央寄せ
    &.center {
      justify-content: center;
    }
    //ワイド
    &.if_16-9 {
      aspect-ratio: 16/9;
    }
    //スタンダート
    &.if_4-3 {
      aspect-ratio: 4/3;
    }
    //スクエア
    &.if_1-1 {
      aspect-ratio: 1/1;
    }
    //縦撮り
    &.if_9-16 {
      aspect-ratio: 9/16;
    }
    //シネマスコープ
    &.if_235-1 {
      aspect-ratio: 2.35/1;
    }
    //カスタムclass
    &.if_custom {
      width: 100%;
      height: 0;
      position: relative;
      $var: 300;
      @while $var <= 3000 {
        &.pc-h#{$var} {
          @include breakpoint-pc {
            height: #{$var}px;
          }
        }
        &.sp-h#{$var} {
          @include breakpoint-sp {
            height: #{$var}px;
          }
        }
        $var: $var + 10;
      }
      iframe {
        width: 100%;
        height: 100%;
        position: absolute;
        top: 0;
        left: 0;
      }
    }
  }
}
//スムーススクロール
html {
  scroll-behavior: smooth !important;
}
//フォームパーツ
.component {
  &.form {
    .form-title {
      color: var(--fo-text-main-color);
    }
    //フォームの必須
    .form-label {
      &.require {
        &::after {
          padding: 2px 7px 4px !important;
          margin-left: 20px;
        }
      }
    }
    input[type=checkbox],
    input[type=radio] {
      min-height: auto;
    }
    .form-submit {
      .popup-link {
        font-weight: $font_weight;
      }
    }
    &.vertical {
      .form-label {
        &::after {
          position: relative;
          top: -2px;
        }
      }
    }
    &.horizontal {
      .form-label {
        padding-right: 10px;
        &::after {
          @include breakpoint-pc {
            top: 8px !important;
          }
          @include breakpoint-sp {
            top: -2px !important;
          }
        }
      }
    }
  }
}
//ズレ対策
.component {
  &.form {
    label.agreement {
      display: flex;
      align-items: center;
      .form-label {
        padding-top: 6px;
        &::after {
          top: -1.5px;
        }
      }
    }
  }
}
//2カラム時、PCとSPでカラムの位置を入れ替える
.reverse {
  @include breakpoint-sp {
    display: flex !important;
    flex-wrap: wrap-reverse;
  }
}
