/* ----------------------------------
  カテゴリ・タグ自動生成ページ
\----------------------------------*/
.theme-fo-base {
  &.clearfix {
    .wrapper {
      &.clearfix {
        .contents {
          :is(
            .category-preview,
            .tag-preview
          ) {
            box-sizing: border-box;
