                width: 100%;
                margin: 20px 0;
                padding: 0;
              }
              .article-thumbnail {
                display: table-cell;
                width: 30%;
                padding-bottom: 10px;
                @include breakpoint-sp {
                  display: block;
                  width: 100%;
                }
                a {
                  display: block;
                  @include breakpoint-sp {
                    margin-bottom: 10px;
                  }
                  img {
                    width: 100%;
                    border-radius: 0;
                  }
                }
              }
              .article-cont {
                display: table-cell;
                width: 70%;
                height: auto;
                vertical-align: top;
                padding: 10px 30px;
                @include breakpoint-sp {
                  display: block;
                  width: 100%;
                  margin-bottom: 20px;
                  padding: 0;
                }
                .article-title {
                  overflow: initial;
                  max-height: none;
                  a {
                    font-family: var(--fo-heading-font-family);
