        }
      }
    }
    // 右サイドバー設定
    &.side-left {}
    // 中央サイドバーセクション
    &.side-both {
      @include breakpoint-pro_s {
        display: flex;
        flex-wrap: wrap;
      }
      .contents {
        @include breakpoint-pc {
          width: 100%;
        }
        @include breakpoint-pro_s {
          display: block;
          max-width: none;
          order: 1;
          padding: 10px 0;
        }
        @include breakpoint-sp {
          padding: 10px 0;
        }
        section {
          .inner {
            .row {
              .col {
                .component {
                  &.form {
                    .form-submit {
                      .popup-link {
                        @include breakpoint-pro {
                          min-width: 100%;
                        }
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
      .side {
        &.left {
          @include breakpoint-pro_s {
            order: 2;
          }
        }
        &.right {
          @include breakpoint-pro_s {
            order: 3;
          }
        }
      }
    }
    // 左サイドバー設定
    &.side-left {
      @include breakpoint-pro_s {
        display: flex;
        flex-wrap: wrap;
        flex-direction: column-reverse;
      }
    }
  }
}
/*--- カテゴリ --- */
.component {
  &.category {
    &.category_custom {
      margin-bottom: 20px !important;
      a {
        color: var(--fo-text-main-color);
        &:hover {
          opacity: $hover_opacity;
        }
      }
    }
  }
}
/*--- ランキング ---*/
.component {
  &.blog-ranking {
    &.ranking_custom {
      @include breakpoint-sp {
        width: 100%;
        margin: auto;
      }
      .entry-list-unit {
        border-color: $blog_border_color;
        @include breakpoint-sp {
          padding: 20px 0 10px;
        }
        .img {
          padding: 0 !important;
          @include breakpoint-sp {
            padding-right: 0 !important;
          }
          img {
            border-radius: 0;
          }
        }
        .cont {
          border-left: 0;
          @include breakpoint-sp {
            padding: 0 !important;
          }
          h3 {
            display: -webkit-box;
            -webkit-box-orient: vertical;
            -webkit-line-clamp: 2;
            overflow: hidden;
            text-overflow: ellipsis;
            color: var(--fo-text-main-color);
            margin: 0 0 10px;
            @include breakpoint-sp {
              margin-top: 1em;
              padding: 0;
            }
            a {
              padding: 0;
              margin: 0;
            }
          }
          p {
            display: -webkit-box;
            -webkit-box-orient: vertical;
            -webkit-line-clamp: 4;
            overflow: hidden;
            text-overflow: ellipsis;
            padding: 0;
          }
        }
        .article-rank {
          background: #fff;
          color: var(--fo-main-color);
          border: 1px solid var(--fo-main-color);
          z-index: 2;
          @include breakpoint-msp {
            min-width: 45px;
            height: 45px;
            font-size: 20px;
            line-height: 45px;
          }
          &:is(.rank-1,.rank-2,.rank-3) {
            background: var(--fo-main-color) !important;
            color: #fff;
            border: 1px solid var(--fo-main-color);
          }
