.theme-fo-base {
  .foot {
    &.ft_custom {
      background: $footer_bg_color;
      background-repeat: no-repeat;
      color: var(--fo-text-main-color);
      padding: 0;
      .foot-cont {
        padding: 60px 20px 70px;
        @include breakpoint-msp {
          padding: 40px 20px;
        }
        .row {
          //ロゴの部分
          &.ft_logo_row {
            & \+ .ft_site_map_row {
              @include breakpoint-pc {
                margin-top: 30px;
              }
              @include breakpoint-msp {
                margin-top: 20px;
              }
            }
          }
          &.ft_site_map_row {
            @include breakpoint-tb {
              display: flex;
              gap: 10px;
            }
            & \+ .ft_bottom_row {
              margin-top: 35px;
              @include breakpoint-ssp {
                margin-top: 15px;
              }
            }
            //各colの幅やスタイル部分
            .col {
              vertical-align: top;
              //リンクリストの部分
              &.site_map_col {
                & \+ .site_map_col {
                  @include breakpoint-ssp {
                    padding-top: 10px;
                  }
                }
              }
            }
          }
          //ボタン固定
          &.page_top_row {
            @include page_top;
          }
          .col {
            @include breakpoint-sp {
              margin: 0;
              padding: 0;
            }
            .component {
              //イメージ
              &.image {
                &.ft_logo_img {
                  img {
                    max-width: 244px !important;
                    width: 100% !important;
                  }
                }
              }
              //リスト
              &.list {
                &.site_map_list {
                  margin: 0;
                  ul {
                    font-size: inherit;
                    li {
                      list-style-type: none;
                      font-size: inherit;
                      color: inherit;
                      &:not(:first-child) {
                        margin: 8px 0 0;
                      }
                      a {
                        display: inline-block;
                        color: inherit;
                        padding: 0;
                        &:hover {
                          background: transparent;
                          opacity: $hover_opacity;
                        }
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
      //コピーライト部分
      .foot-copyright {
        @include copyright_custom;
      }
    }
  }
}
