//セクション余白
// ()内に指定したいサイズを入れると適用されます。例：(100px)
// ()内に数値が入ってない場合は適用されません。（デフォルト設定）
section[data-designpart-id]:not(:root:root:root:root) {
  @include sectionSpace();
}
//コンテンツ幅
@include theme-fo-base {
  .inner {
    max-width: $max_width;
  }
}
/*--- リッチテキストパーツ ---*/
.component {
  &.richtext {
    :link {
      text-decoration: none;
    }
    :visited {
      text-decoration: none;
    }
    //リスト
    li {
      font-size: inherit;
      a {
        font-size: inherit;
      }
    }
    //表
    table {
      border: none;
      &[align="center"] {
        margin: auto;
      }
      :is(thead,tbody,tr,th,td) {
        border-color: $border_color;
        tr {
          :is(th,td) {
            background-clip: padding-box; //FF,edgeでボーダー消える現象回避;
            position: static; //FF,edgeでボーダー消える現象回避;
            p {
              font-size: inherit;
              & \+ p {
                margin-top: 10px !important;
              }
            }
            :is(ul,ol) {
              margin-left: 25px;
            }
          }
        }
      }
    }
    .normal-table {
      &::after {
        content: "";
        display: table;
        clear: both;
      }
    }
    .table-with-row-header {
      &::after {
        content: "";
        display: table;
        clear: both;
      }
    }
    .table-with-two-headers {
      overflow: auto;
      &::after {
        content: "";
        display: table;
        clear: both;
      }
    }
    //sp時右寄せ
    &.sp_r {
      @include breakpoint-sp {
        text-align: right !important;
      }
      :is(p,.rich-text-normal,.align-center,.align-right) {
        @include breakpoint-sp {
          text-align: right !important;
        }
      }
    }
    //sp中央寄せ
    &.sp_c {
      @include breakpoint-sp {
        text-align: center !important;
      }
      :is(p,.rich-text-normal,.align-center,.align-right) {
        @include breakpoint-sp {
          text-align: center !important;
        }
      }
    }
    //sp時左寄せ
    &.sp_l {
      @include breakpoint-sp {
        text-align: left !important;
      }
      :is(p,.rich-text-normal,.align-center,.align-right) {
        @include breakpoint-sp {
          text-align: left !important;
        }
      }
    }
  }
}
/*--- 区切り線パーツ ---*/
.theme-fo-base {
  :is(.main-visual,.wrapper :is(.side,.contents),.foot ) {
    .component {
      &.border {
        hr {
          &.type01 {
            background-image: -webkit-linear-gradient(left, transparent, $border_color, transparent);
          }
          &:is(.type02,.type03,.type04) {
            border-color: $border_color;
          }
        }
      }
    }
  }
}
/*--- CTA(共通パーツ)---*/
.component {
  &.cta {
    .cta-row {
      display: table;
      table-layout: fixed;
      width: 100%;
      margin: 0;
      @include breakpoint-sp {
        display: block;
      }
      & \+ .cta-row {
        @include breakpoint-pc {
          padding-top: 10px;
        }
      }
      .cta-column {
        display: table-cell;
        vertical-align: top;
        margin: 0;
        @include breakpoint-sp {
          display: block;
          width: 100% !important;
        }
      }
    }
  }
}
/*---  リンク埋め込みパーツ---*/
.component {
  // ページ
  &.link-embed {
    .article-embed {
      .quote-link {
        &:hover {
          opacity: $hover_opacity;
        }
        .quote-link_entry {
          .quote-link-img {
            .quote-link-img-item {
              img {
                height: 150px;
                position: static;
                object-fit: cover;
                &:hover {
                  opacity: 1;
                }
              }
            }
          }
          .cont {
            :is(.quote-link_title,.quote-link_desp,.quote-link_site) {
              p {
                color: var(--fo-text-main-color);
              }
            }
          }
        }
      }
    }
  }
  //ブログ
  &.richtext {
    .article-embed[data-embed-type="url"] {
      .quote-link {
        .quote-link_entry {
          .quote-link-img {
            .quote-link-img-item {
              background-position: center;
            }
          }
          .cont {
            .quote-link_desp {
              margin-bottom: 10px;
            }
          }
        }
      }
