  .wrapper {
    //１カラム用
    &.none {
      .contents {
        & > .component {
          &.search {
            margin: 50px auto;
          }
        }
      }
    }
    // 2~3カラム
    &:is(.side-right,.side-both,.side-left) {
      .contents {
        & > .component {
          &.search {
            margin: 30px auto 50px;
            @include breakpoint-sp {
              margin-top: 50px;
            }
          }
        }
      }
    }
