            font-family: var(--fo-heading-font-family);
            font-size: $list_heading_pc_fontsize;
            color: var(--fo-heading-main-color);
            line-height: var(--fo-heading-line-height);
            letter-spacing: var(--fo-heading-letter-spacing);
            @include breakpoint-sp {
              font-size: $list_heading_sp_fontsize;
            }
            &:hover {
              opacity: $hover_opacity;
            }
          }
        }
        h4 {
          font-size: var(--fo-text-font-size-pc);
          letter-spacing: var(--fo-text-letter-spacing);
          color: var(--fo-text-main-color);
        }
        p {
          &.category {
            a {
              &:hover {
                opacity: $hover_opacity;
              }
            }
          }
          &.description {
            max-height: none;
            font-size: var(--fo-text-font-size-pc);
            letter-spacing: var(--fo-text-letter-spacing);
            line-height: var(--fo-text-line-height);
            color: var(--fo-text-main-color);
            @include breakpoint-sp {
              font-size: var(--fo-text-font-size-sp);
              margin: 10px 0;
              padding: 0;
            }
          }
        }
      }
    }
    //カード型
    &:is(
      .entry-image-card-1-col,
      .entry-image-card-2-col,
      .entry-image-card-3-col
    ) {
      .article-list {
        .entry-list-unit {
          .img {
            @include breakpoint-sp {
              padding-right: 0;
            }
          }
          .cont {
            h3 {
              a {
                @include breakpoint-pc {
                  padding: 0 !important;
                }
              }
            }
            h4 {
              margin: 10px 0;
              @include breakpoint-pc {
                padding: 0 !important;
              }
            }
            p {
              &.category {
                margin: 10px 0 10px 3px;
              }
            }
            div {
              &.tags {
                margin: 0 0 5px;
                padding: 0;
              }
            }
          }
        }
      }
    }
    //リスト型・スタイル選択
    .entry-list-unit {
      border-bottom: 1px solid $blog_border_color !important;
      .img {
        @include breakpoint-pc {
          padding-right: 25px;
        }
      }
      .cont {
        a {
          &:hover {
            text-decoration: none;
          }
        }
        //タイトル
        h3 {
          a {
            @include breakpoint-pc {
              padding: 0 10px;
            }
          }
        }
        h4 {
          padding: 0 10px;
          @include breakpoint-sp {
            padding: 0;
          }
        }
        p {
          &.category {
            margin: 10px;
            @include breakpoint-sp {
              padding-left: 0;
            }
          }
          &.description {}
        }
        //記事リスト共通 タグ
        div {
          &.tags {
            display: flex;
            gap: 5px 10px;
            flex-wrap: wrap;
            margin: 16px 10px 10px;
            padding: 0 10px;
            @include breakpoint-sp {
              margin: 16px 0 10px;
              padding: 0;
            }
            a {
              &.tag {
                margin: 0;
                &:hover {
                  opacity: $hover_opacity;
                }
              }
            }
          }
        }
      }
    }
  }
}
//サイドバー時
.side {
  .component {
    &.entry-list {
      .entry-list-unit {
        .img {
          padding-right: 0;
        }
      }
    }
  }
