/* --------------------------------
    header・logo・navigations（ヘッダー・ロゴ・ナビゲーション）
\----------------------------------- */
.navi_base_custom {
  //1段のとき
  &.navi-001-01 {
    header#navigations {
      min-height: 51px;
      font-family: var(--fo-text-font-family);
      border-bottom: 0;
      box-shadow: $navigation_box_shadow_common;
      .inner {
        @include breakpoint-pc {
          box-sizing: border-box;
          display: flex;
          padding: 0;
        }
      }
      .logo {
        float: none;
        @include breakpoint-pc {
          width: $header_logo;
          padding: 10px;
        }
        @include breakpoint-sp {
          box-sizing: border-box;
          width: 100%;
          padding-left: 10px;
        }
        a {
          height: auto;
          img {
            @include breakpoint-sp {
              max-height: $header_sp_logo;
            }
          }
        }
      }
      //ハンバーガーメニュー
      a.menu-trigger {
        background: var(--fo-main-color);
        span {
          background-color: #fff;
        }
      }
      .main-menu {
        float: none;
        @include breakpoint-pc {
          width: calc(100% - #{$header_logo});
          max-width: none;
        }
        &.navigation-font-family {}
        ul.clearfix {
          background: $navigation_bg_color;
        }
        //PC用
        @include breakpoint-pc {
          .level-01,
          .level-01-trigger {
            a {
              font-size: $navigation_font_size;
              font-weight: $navigation_font_weight;
              color: $navigation_text_color;
              border-left: none;
              border-right: none;
              padding: 25px 10px;
            }
          }
          .level-01:hover {
            background-color: $navigation_hover_bg_color;
            a {
              color: $navigation_hover_text_color;
              opacity: $navigation_hover_opacity;
            }
          }
          .level-01-trigger {
            //三角部分
            &::after {
              content: "\f107";
              font-size: 20px;
              font-weight: 400;
              line-height: 1;
              color: $navigation_arrow1_color;
              translate: 0 -50%;
              margin-top: 0;
            }
            &:hover::after {
              color: $navigation_hover_text_color;
              rotate: 180deg;
            }
            &:hover {
              background: $navigation_hover_bg_color;
              & > a {
                color: $navigation_hover_text_color;
                opacity: $navigation_hover_opacity;
              }
            }
            ul.level-01-cont {
              background: $navigation_bg2_color;
              border: 0;
              a {
                color: $navigation_text2_color;
                padding: 20px;
                &:hover {
                  background: $navigation_hover_bg2_color;
                  color: $navigation_hover_text2_color;
                }
              }
              .level-02-trigger {
                &::after {
                  content: "\f105";
                  font-size: 20px;
                  font-weight: 400;
                  color: $navigation_arrow2_color;
                  right: 10px;
                  translate: 0 -50%;
                  margin-top: 0;
                }
                &:has(> a:hover) {
                  &::after {
                    color: $navigation_hover_text2_color;
                  }
                }
                .level-02-cont {
                  background: $navigation_bg2_color;
                  li {}
                  .level-03 {
                    a {
                      padding: 20px 10px;
                    }
                  }
                }
              }
            }
          }
        }
        //SP用は共通ハンバーガー
      }
    }
  }
  //2段のとき
  &.navi-002-01 {
    font-family: var(--fo-text-font-family);
    header#navigations {
      background: $header_bg_color;
      box-shadow: $navigation_box_shadow_common;
