//メインビジュアル
.theme-fo-base {
  .main-visual {
    background-repeat: no-repeat;
    min-height: auto;
    padding: $mv_padding;
    @include breakpoint-sp {
      padding: $sp_mv_padding;
    }
    @include breakpoint-tb {
      min-height: auto;
    }
    & > :is(.clearfix,.main-visual-content) {
        width: 100%;
        max-width: $max_width;
        display: flex;
        flex-direction: column;
        justify-content: center;
        min-height: auto;
        margin: auto;
        padding: 0 20px;
        @include breakpoint-sp {
          min-height: auto;
        }
      .row {
        .col {
          .component {
            &.heading {
              &.hd_custom {
                :is(h1,h2,h3,h4,h5) {
                  margin-bottom: 0;
                }
                h1 {
                  @include breakpoint-pc {
                    font-size: calc(var(--fo-h1-font-size-pc) + 4px);
                  }
                }
              }
            }
          }
        }
      }
    }
    //TOP
    &.mv_custom {
      & > :is(.clearfix,.main-visual-content) {
        min-height: $top_mv_height;
        @include breakpoint-sp {
          min-height: auto;
        }
        .row {
          \--fo-heading-main-color: #fff;
          \--fo-text-main-color: #fff;
          .col {
            .component {
              &.hd_custom {
                h1{}
              }
            }
          }
        }
      }
    }
    //下層
    &.mv_sub_custom {
      & > :is(.clearfix,.main-visual-content) {
        min-height: $sub_mv_height;
        .row {
          \--fo-heading-main-color: #fff;
          \--fo-text-main-color: #fff;
          \--fo-text-link-color: #fff;
          .col {
            .component {
              &.cta {
                .cta-row {
                  &.mv_design01_row {
                    margin-top: 22px;
                    .cta-column {
                      display: flex;
                      flex-direction: column;
                      gap: 8px;
                      width: 100%;
                      .component {
                        &.button[data-designpart-id] {
                          margin: 0;
                        }
                      }
                    }
                  }
                }
              }
              &.hd_custom {
                h1{}
              }
            }
          }
        }
      }
    }
    //幅100％
    &.maxWidth_custom {
      & > :is(.clearfix,.main-visual-content) {
        max-width: none;
      }
    }
  }
}
