/*--- ブログページ一覧 ---*/
.component {
  &.blog_articles {
    hr {
      border: 0;
      border-bottom: 1px solid $blog_border_color;
      margin-bottom: 30px;
      @include breakpoint-sp {
        margin-bottom: 10px;
      }
    }
    article {
      .infomation {
        padding: 0 0 0 30px;
        //タイトル
        & > h2 {
          line-height: 1.5;
          padding: 0;
          a {
            display: inline-block;
            font-family: var(--fo-heading-font-family);
            font-size: $list_heading_pc_fontsize;
            font-weight: bold;
            letter-spacing: var(--fo-heading-letter-spacing);
            line-height: var(--fo-heading-line-height);
            color: var(--fo-heading-main-color);
            @include breakpoint-sp {
              font-size: $list_heading_sp_fontsize;
            }
            &:hover {
              opacity: $hover_opacity;
            }
          }
        }
        p {
          font-size: var(--fo-text-font-size-pc);
          color: var(--fo-text-main-color);
          line-height: var(--fo-text-line-height);
          letter-spacing: var(--fo-text-letter-spacing);
          padding: 0;
          @include breakpoint-sp {
            font-size: var(--fo-text-font-size-sp);
          }
        }
        //記事を見るの位置
        a {
          &.view-article {
            display: inline-block;
            width: 100%;
            font-size: var(--fo-text-font-size-pc);
            color: var(--fo-text-link-color,var(--fo-text-main-color));
            line-height: var(--fo-text-line-height);
            letter-spacing: var(--fo-text-letter-spacing);
            text-align: right;
            text-decoration: underline;
            @include breakpoint-sp {
              font-size: var(--fo-text-font-size-sp);
            }
            &:hover {
              opacity: $hover_opacity;
              text-decoration: none;
            }
          }
        }
        //日付の背景色
        span {
          &.published_at {
            background: none;
            font-size: 14px;
            color: var(--fo-text-main-color);
            text-align: left;
            padding: .5rem 0;
          }
        }
        //タグスタイル
        .tags {
          padding-left: 0;
          a {
            &.tag {
              @include tag_custom;
              i {
                margin-right: 0;
                &::before {
                  @include tag_icon_custom;
                }
              }
            }
          }
        }
      }
      .catch,
      .infomation {
        @include breakpoint-sp {
          width: 100%;
          float: none;
          padding: 1em 0 0;
        }
      }
      .catch {
        a {
          img {
            border-radius: $blog_border_radius;
          }
        }
      }
    }
  }
}
/*--- ブログ記事詳細 ---*/
.theme-fo-base {
  .clearfix {
    .contents {
      &.blog-contents {
        padding: 0;
        section {
          .inner {
            @include breakpoint-sp {
              padding: 0 20px 10px;
            }
            .row {
              .col {
                .component {
                  &.blog_article {
                    margin-top: 0;
                    // 日付・タグ
                    .article-meta-info {
                      opacity: unset;
                      span {
                        &.post-date {
                          time {
                            font-family: var(--fo-text-font-family);
                          }
                        }
                        &.article-category {
                          i {
                            display: none;
                          }
                          a {
                            color: var(--fo-main-color);
                            &::before {
                              @include category_icon_custom;
                            }
                            &:hover {
                              opacity: $hover_opacity;
                            }
                          }
                        }
                        &.article-tags {
                          i {
                            display: none;
                          }
                          a {
                            @include tag_custom;
                            display: inline-block;
                            margin-top: 10px;
                            &::before {
                              @include tag_icon_custom;
                            }
                            &::after {
                              content: none;
                            }
                          }
                        }
                      }
                    }
                    // アイキャッチ
                    .article-eyecatch {
                      @include breakpoint-pc {
                        margin-bottom: 30px;
                      }
                      @include breakpoint-sp {
                        margin: 30px 0;
                      }
                    }
                    .article-post {
                      color: var(--fo-text-main-color);
                      padding: 0;
                      // ブログ詳細の本文
                      .component {
                        &.richtext {
                          // 引用
                          blockquote {
                            font-size: inherit;
                            color: inherit;
                            padding: 40px 30px;
                            @include breakpoint-sp {
                              padding: 20px;
                            }
                            &::before {
                              font-size: 500%;
                            }
                          }
                        }
                      }
                    }
                    section {
                      //著者
                      &.author {
                        padding: 0;
                        border-color: $border_color;
                        @include breakpoint-sp {
                          display: flex;
                          padding: 15px;
                        }
                        .author-image {
                          margin-right: 15px;
                          @include breakpoint-sp {
                            width: 50%;
                          }
                          img {
                            border-radius: $blog_border_radius;
                          }
                        }
                        .author-info {
                          .author-name {
                            font-family: var(--fo-heading-font-family);
                            font-size: 18px;
                            color: var(--fo-heading-main-color);
                            line-height: var(--fo-heading-line-height);
                            letter-spacing: var(--fo-heading-letter-spacing);
                            @include breakpoint-sp {
                              font-size: 16px;
                            }
                          }
                          .author-description {
                            white-space: pre-wrap;
                            font-size: var(--fo-text-font-size-pc);
                            letter-spacing: var(--fo-text-letter-spacing);
                            line-height: var(--fo-text-line-height);
                            color: var(--fo-text-main-color);
                            @include breakpoint-sp {
                              font-size: var(--fo-text-font-size-sp);
                            }
                          }
                        }
                      }
                    }
                  }
                }
                section {
                  //ページネーション
                  &.prev-next-link {
                    display: flex;
                    gap: calc(40px * var(--fo-spacing-coef));
                    @include breakpoint-sp {
                      flex-direction: column;
                      gap: 0;
                      margin: calc((40px * var(--fo-spacing-coef)) * .75) 0 20px;
                      padding: 0;
                    }
                    a {
                      color: var(--fo-text-main-color);
                      border-color: $border_color;
                      &:hover {
                        background: transparent;
                        opacity: $hover_opacity;
                      }
                      &::before,
                      &::after {
                        color: var(--fo-main-color);
                      }
                      //前の記事 次の記事
                      h3 {
                        &.prev-next-label {
                          font-family: var(--fo-heading-font-family);
                          font-size: 18px;
                          color: var(--fo-heading-main-color);
                          line-height: var(--fo-heading-line-height);
                          letter-spacing: var(--fo-heading-letter-spacing);
                          @include breakpoint-sp {
                            font-size: 16px;
                          }
                        }
                      }
                      div.thumb-wrap {
                        display: flex;
                        align-items: flex-start;
                        img {
                          float: none;
                          margin-right: 15px;
                        }
                        p {
                          max-height: none;
                          font-size: 14px;
                          letter-spacing: var(--fo-text-letter-spacing);
                          line-height: var(--fo-text-line-height);
                          color: var(--fo-text-main-color);
                          display: -webkit-box;
                          -webkit-line-clamp: 2;
                          -webkit-box-orient: vertical;
                          text-overflow: ellipsis;
                          overflow: hidden;
                        }
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
    }
  }
