/* ----------------------------------
  カテゴリ・タグ自動生成ページ
\----------------------------------*/
.theme-fo-base {
  &.clearfix {
    .wrapper {
      &.clearfix {
        .contents {
          :is(
            .category-preview,
            .tag-preview
          ) {
            box-sizing: border-box;
            margin: auto;
            article {
              display: table;
              border-bottom: 1px solid $blog_border_color;
              @include breakpoint-sp {
                display: block;
                width: 100%;
                margin: 20px 0;
                padding: 0;
              }
              .article-thumbnail {
                display: table-cell;
                width: 30%;
                padding-bottom: 10px;
                @include breakpoint-sp {
                  display: block;
                  width: 100%;
                }
                a {
                  display: block;
                  @include breakpoint-sp {
                    margin-bottom: 10px;
                  }
                  img {
                    width: 100%;
                    border-radius: 0;
                  }
                }
              }
              .article-cont {
                display: table-cell;
                width: 70%;
                height: auto;
                vertical-align: top;
                padding: 10px 30px;
                @include breakpoint-sp {
                  display: block;
                  width: 100%;
                  margin-bottom: 20px;
                  padding: 0;
                }
                .article-title {
                  overflow: initial;
                  max-height: none;
                  a {
                    font-family: var(--fo-heading-font-family);
                    font-size: $list_heading_pc_fontsize;
                    font-weight: inherit;
                    color: var(--fo-heading-main-color);
                    line-height: var(--fo-heading-line-height);
                    letter-spacing: var(--fo-heading-letter-spacing);
                    @include breakpoint-sp {
                      font-size: $list_heading_sp_fontsize;
                    }
                    &:hover {
                      opacity: $hover_opacity;
                    }
                  }
                }
                .article-meta-info {
                  position: relative;
                  .article-category {
                    &::before {
                      content: none;
                    }
                    a {
                      color: var(--fo-text-link-color,var(--fo-text-main-color));
                      &::before {
                        @include category_icon_custom;
                      }
                      &:hover {
                        opacity: $hover_opacity;
                      }
                    }
                  }
                  //タグ自動生成のタグスタイル
                  .tag-lists {
                    @include breakpoint-sp {
                      display: block;
                    }
                    .article-tags {
                      @include tag_custom;
                      &::before {
                        @include tag_icon_custom;
                      }
                    }
                  }
