}
/*--- ランキング ---*/
.component {
  &.blog-ranking {
    &.ranking_custom {
      @include breakpoint-sp {
        width: 100%;
        margin: auto;
      }
      .entry-list-unit {
        border-color: $blog_border_color;
        @include breakpoint-sp {
          padding: 20px 0 10px;
        }
        .img {
          padding: 0 !important;
          @include breakpoint-sp {
            padding-right: 0 !important;
          }
          img {
            border-radius: 0;
          }
        }
        .cont {
          border-left: 0;
          @include breakpoint-sp {
            padding: 0 !important;
          }
          h3 {
            display: -webkit-box;
            -webkit-box-orient: vertical;
            -webkit-line-clamp: 2;
            overflow: hidden;
            text-overflow: ellipsis;
            color: var(--fo-text-main-color);
            margin: 0 0 10px;
            @include breakpoint-sp {
              margin-top: 1em;
              padding: 0;
            }
            a {
              padding: 0;
              margin: 0;
            }
          }
          p {
            display: -webkit-box;
            -webkit-box-orient: vertical;
            -webkit-line-clamp: 4;
            overflow: hidden;
            text-overflow: ellipsis;
            padding: 0;
          }
        }
        .article-rank {
          background: #fff;
          color: var(--fo-main-color);
          border: 1px solid var(--fo-main-color);
          z-index: 2;
          @include breakpoint-msp {
            min-width: 45px;
            height: 45px;
            font-size: 20px;
            line-height: 45px;
          }
          &:is(.rank-1,.rank-2,.rank-3) {
