/*----------------------------------
    LP body（本体）
\----------------------------------*/
@include theme-fo-base {
  //LPヘッダー
  &.#{$lp-theme}_header_sec {
    @include lp-variables;
    padding: 0;
    .inner {
      padding: 10px 20px;
      @include breakpoint-sp {
        padding: 10px;
      }
      .row {
        &.header_row {
          display: flex;
          align-items: center;
          @include breakpoint-pc {
            min-height: 60px;
          }
          .col {
            padding: 0;
            &.logo_col {
              @include breakpoint-pc {
                max-width: 300px;
                min-width: 150px;
                width: auto !important;
              }
            }
            &.btn_col {
              display: flex !important;
              justify-content: flex-end;
              align-items: center;
              flex: 1;
              @include breakpoint-pc {
                width: auto !important;
              }
              .component {
                margin: 0;
                &:not(:first-child) {
                  @include breakpoint-pc {
                    margin-left: 15px;
                  }
                }
                &.button {
                  margin-top: 0;
                  .button-cont {
                    a {
                      @include breakpoint-sp {
                        background: none;
                        width: auto;
                        min-width: auto;
                        font-size: 0 !important;
                        letter-spacing: 0;
                        border-radius: 0;
                        border: 0;
                        margin: 0;
                        box-shadow: none;
                        padding: 5px 10px 5px 5px;
                      }
                      &::before {
                        @include breakpoint-sp {
                          font-size: 22px;
                          line-height: 1;
                          color: var(--fo-main-color);
                          margin: 0;
                        }
                      }
                    }
                  }
                }
              }
            }
          }
        }
        .col {
          .component {
            &.image {
              margin: 0;
              &.#{$lp-theme}_logo_img {
                img {
                  @include breakpoint-pc {
                    max-width: var(--lp_header_logo) !important;
                    width: 100% !important;
                  }
                  @include breakpoint-sp {
                    max-height: var(--lp_header_sp_logo) !important;
                  }
                }
              }
            }
          }
        }
        &.nav_row {
          @include breakpoint-pc {
            margin: 20px 0 -10px;
          }
          .col {
            padding: 0;
            .component {
              &.list {
                margin: 0;
                ul {
                  @include breakpoint-pc {
                    display: table;
                    width: 100%;
                    table-layout: fixed;
                  }
                  li {
                    margin: 0;
                    padding: 0;
                    font-size: 14px;
                    @include breakpoint-pc {
                      display: table-cell;
                    }
                    @include breakpoint-sp {
                      width: 50%;
                    }
                    a {
                      display: block;
                      font-size: inherit;
                      color: inherit;
                      text-align: center;
                      padding: 20px 10px;
                      @include breakpoint-sp {
                        padding: 10px 5px;
                      }
                      &:hover {
                        background-color: var(--fo-main-color);
                        color: #fff;
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
    }
    & ~ section {
      @include lp-variables;
      //セクション余白調整(デフォルトパーツorカスタム用)
      // @include breakpoint-pc {
      //   padding: calc(var(--lp_section_padding) - 30px) 0 calc(var(--lp_section_padding) - 40px);
      // }
      // @include breakpoint-sp {
      //   padding: calc(var(--lp_section_sp_padding) - 30px) 0;
      // }
      // コンテンツはここから
      .inner {
        .row {
          .col {
            .component {
              &.richtext {}
              &.image {}
              &.list {}
            }
          }
        }
      }
      // メインビジュアル
      &.#{$lp-theme}_mv_sec {
        padding: 0;
      }
      // cta
      &.#{$lp-theme}_cta_sec {
        .inner {
          .row {
            .col {
              .component {
                &.cta {
                  margin: 0;
                }
              }
            }
          }
        }
      }
      //フッター
      &.#{$lp-theme}_footer_sec {
        padding: 0;
        .inner {
          max-width: 100% !important;
          padding: 0;
          .row {
            padding: 0;
            &:not(.copyright_row) {
              max-width: var(--lp_max_width);
              margin: 0 auto;
              padding: 0 20px;
            }
            &.site_map_row {
              padding: 40px 20px;
              .col {
                vertical-align: middle;
              }
            }
            &.page_top_row {
              @include page_top;
            }
            &.copyright_row {
              .col {
                padding: 0;
                .component {
                  &.richtext {
                    &.foot-copyright {
                      @include copyright_custom;
                    }
                  }
                }
              }
            }
            .col {
              padding: 0;
              .component {
                &.image {
                  &.footLogo_img {
                    img {
                      max-width: 250px !important;
                      width: 100% !important;
                    }
                  }
                }
                &.list {
                  &.site_map_list {
                    ul {
                      display: flex;
                      gap: 10px 20px;
                      @include breakpoint-sp {
                        flex-direction: column;
                      }
                      li {
                        margin: 0;
                        padding: 0;
                        a {
                          color: inherit;
                          &:hover {
                            opacity: var(--fo-text-link-hover-opacity);
                          }
                        }
                      }
                    }
                    &.vertical {
                      ul {
                        flex-direction: column;
                        li {
                          a {
                            padding: 0;
                          }
                        }
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
    }
  }
}
/*------------- LP（フルカスタム用） END-------------*/
// 編集画面用
@include edit-page {
  .theme-fo-base {
    .wrapper {
      .contents {
        section {
          &.lp_footer_sec {
            .inner {
              .row {
                &.page_top_row {
                  .col {
                    .component {
                      &.image {
                        position: static !important;
                        margin: 10px 0 !important;
                        &::before {
                          content: "ページ右下に固定されます";
                          display: inline-block;
                          text-align: center;
                          background: #0987d4;
                          color: #fff;
                          position: absolute;
                          right: 0;
                          bottom: -24px;
                          left: 0;
                          z-index: 1;
                          padding: 0 5px;
                        }
                      }
                    }
                  }
                }
                &.copyright_row {
                  margin-top: 30px;
                }
              }
            }
          }
          .inner {
            .row {
              .col {
                .component {
                  &.heading {
                    &.lp_inPageLink {
                      margin-top: 10px;
                      padding-top: 0;
                    }
                  }
                }
              }
            }
          }
        }
      }
    }
  }
}
